const input = $input.all();

// Processa cada item do input
return input.map(item => {
  let formattedMessage = '';

  // Verifica se messages existe e √© uma string
  if (item.json.messages && typeof item.json.messages === 'string') {
    formattedMessage = item.json.messages.trim();
  } else {
    formattedMessage = '';
  }

  // Retorna o objeto no formato esperado
  return {
    json: {
      messages: formattedMessage,
      sessionid: item.json.sessionid || ''
    }
  };
});

