const input = $input.all()[0];

try {
  const body = input.json.body?.body;

  // Valida√ß√µes obrigat√≥rias
  if (!body?.data?.key?.id) {
    throw new Error('ID da mensagem n√£o encontrado');
  }

  if (!body?.data?.key?.remoteJid) {
    throw new Error('Telefone do cliente n√£o encontrado');
  }

  if (!body?.instance) {
    throw new Error('Inst√¢ncia n√£o encontrada');
  }

  if (!body?.apikey) {
    throw new Error('API Key n√£o encontrada');
  }

  // Garante campos obrigat√≥rios
  const validatedData = {
    ...input.json,
    body: {
      ...input.json.body,
      body: {
        ...body,
        data: {
          ...body.data,
